mapper.select:
select

from sys_api t

    t.del_flag = 0 AND t.id = #{id}

SELECT
u.id AS id, u.user_name AS userName, u.password AS password, u.work_num AS workNum, u.name AS name, u.email AS email, u.phone AS phone, u.mobile AS mobile, u.user_type AS userType, u.photo AS photo, u.nick_name AS nickName, u.new_password AS newPassword, u.account_non_expired AS accountNonExpired, u.account_non_locked AS accountNonLocked, u.credentials_non_expired AS credentialsNonExpired, u.enabled AS enabled, u.client_id AS clientId, u.create_user_id AS createUserId, u.create_time AS createTime, u.update_user_id AS updateUserId, u.update_time AS updateTime, u.todo AS todo, u.remarks AS remarks, u.del_flag AS delFlag, u.version AS version,
r.id AS Rid, r.name AS Rname, r.role_type AS RroleType, r.is_sys AS RisSys, r.enabled AS Renabled, r.create_user_id AS RcreateUserId, r.create_time AS RcreateTime, r.update_user_id AS RupdateUserId, r.update_time AS RupdateTime, r.todo AS Rtodo, r.remarks AS Rremarks, r.del_flag AS RdelFlag, r.version AS Rversion,
a.id AS Aid, a.parent_id AS AparentId, a.is_menu AS AisMenu, a.name AS Aname, a.path AS Apath, a.sort AS Asort, a.icon AS Aicon, a.component AS Acomponent, a.redirect AS Aredirect, a.hide_in_menu AS AhideInMenu, a.hide_children_in_menu AS AhideChildrenInMenu, a.create_user_id AS AcreateUserId, a.create_time AS AcreateTime, a.update_user_id AS AupdateUserId, a.update_time AS AupdateTime, a.todo AS Atodo, a.remarks AS Aremarks, a.del_flag AS AdelFlag, a.version AS Aversion,
au.id AS AUid, au.name AS AUname, au.code AS AUcode, au.create_user_id AS AUcreateUserId, au.create_time AS AUcreateTime, au.update_user_id AS AUupdateUserId, au.update_time AS AUupdateTime, au.todo AS AUtodo, au.remarks AS AUremarks, au.del_flag AS AUdelFlag, au.version AS AUversion
FROM sys_user u LEFT JOIN sys_user_role ur on ur.user_id = u.id LEFT JOIN sys_role r on r.id = ur.role_id LEFT JOIN sys_role_api ra on ra.role_id = r.id LEFT JOIN sys_api a on a.id = ra.api_id LEFT JOIN sys_api_authority sau on sau.api_id = a.id LEFT JOIN sys_authority au on au.id = sau.authority_id
u.del_flag = 0
AND u.email = #{email}
AND u.mobile = #{mobile}
AND u.user_name = #{userName}
